mapper.select:
		SELECT
			a.*
			,(SELECT codeGroup FROM CCG b where a.ccgSeq = b.ccgSeq) as code
            ,(SELECT codeGroupK FROM CCG b where a.ccgSeq = b.ccgSeq) as codeK
		FROM CC a
		Where 1=1
			and a.ccUseNy= 1
			AND ccDelNy = #{shDelNy}

				AND a.ccSeq = #{shValue}
				AND a.ccCodeName = #{shValue}
				AND a.codeGroupK = #{shValue}
				AND a.codeGroup = #{shValue}



				AND a.ccDate BETWEEN #{shStartDate} AND #{shEndDate}

mapper.insert:
INSERT INTO CC (
	ccgSeq
	,ccCodeName
	,ccUseNy
	,ccDelNy
	,ORDER
)VALUES(
	#{ccgSeq}
	,#{ccCodeName}
	,#{ccUseNy}
	,0
	,#{ORDER}
)

		SELECT last_insert_id()
