mapper.sql:
    	FROM CC a
    	INNER JOIN CCG b
    	ON a.ccgSeq = b.ccgSeq
    	HAVING 1=1
			AND ccDelNy = #{shDelNy}

				AND a.ccSeq = #{shValue}
				AND codeK LIKE CONCAT('%', #{shValue}, '%')
				AND code LIKE CONCAT('%', #{shValue}, '%')
				AND a.ccCodeName LIKE CONCAT('%', #{shValue}, '%')



				AND a.ccDate BETWEEN #{shStartDate} AND #{shEndDate}

mapper.insert:
INSERT INTO CC (
	ccgSeq
	,ccCodeName
	,ccUseNy
	,ccDelNy
	,ccOrder
)VALUES(
	#{ccgSeq}
	,#{ccCodeName}
	,#{ccUseNy}
	,0
	,#{ccOrder}
)

		SELECT last_insert_id()

mapper.update:
UPDATE CC
SET 
ccCodeName = #{ccCodeName}
,ccUseNy = #{ccUseNy}
,ccDelNy = #{ccDelNy}
,ccOrder = #{ccOrder}
WHERE 1=1
	AND ccSeq = #{ccSeq}
