mapper.select:
SELECT a.ccSeq ,a.ccgSeq ,a.ccCodeName ,a.ccUseNy ,a.ORDER ,a.ccDate ,a.ccDelNy ,b.codeGroup as code ,b.codeGroupK as codeK FROM CC a INNER JOIN CCG b ON a.ccgSeq = b.ccgSeq HAVING 1=1 AND ccDelNy = #{shDelNy}
AND a.ccSeq = #{shValue} AND codeK LIKE CONCAT('%', #{shValue}, '%') AND code LIKE CONCAT('%', #{shValue}, '%') AND a.ccCodeName LIKE CONCAT('%', #{shValue}, '%')
AND a.ccDate BETWEEN #{shStartDate} AND #{shEndDate}

mapper.insert:
INSERT INTO CC (
	ccgSeq
	,ccCodeName
	,ccUseNy
	,ccDelNy
	,ORDER
)VALUES(
	#{ccgSeq}
	,#{ccCodeName}
	,#{ccUseNy}
	,0
	,#{ORDER}
)

		SELECT last_insert_id()
