mapper.select:
SELECT a.* ,(SELECT codeGroup FROM CCG b where a.ccgSeq = b.ccgSeq) as code ,(SELECT codeGroupK FROM CCG b where a.ccgSeq = b.ccgSeq) as codeK FROM CC a Where 1=1 AND ccDelNy = #{shDelNy}
AND ccSeq = #{shValue} AND codeGroupK LIKE CONCAT('%', #{shValue}, '%') AND codeGroup LIKE CONCAT('%', #{shValue}, '%') AND ccCodeName LIKE CONCAT('%', #{shValue}, '%')
AND a.ccDate BETWEEN #{shStartDate} AND #{shEndDate}

mapper.insert:
INSERT INTO CC (
	ccgSeq
	,ccCodeName
	,ccUseNy
	,ccDelNy
	,ORDER
)VALUES(
	#{ccgSeq}
	,#{ccCodeName}
	,#{ccUseNy}
	,0
	,#{ORDER}
)

		SELECT last_insert_id()
