mapper.select:
SELECT
	a.*
	,(SELECT COUNT(*) FROM CC b where b.ccgSeq = a.ccgSeq) as cnt
FROM CCG a
Where 1=1

	AND ccgDelNy = #{shDelNy}

		AND a.ccgSeq = #{shValue}
		AND a.codeGroupK LIKE CONCAT('%',#{shValue},'%')
		AND a.codeGroup LIKE CONCAT('%',#{shValue},'%')



		AND a.ccgDate BETWEEN #{shStartDate} AND #{shEndDate}

mapper.insert:
INSERT INTO CCG (
	codeGroup
	,codeGroupK
	,ccgDelNy
	,ccgUseNy
	,ccgOrder
	,reference
)Values (
	#{codeGroup}
	,#{codeGroupK}
	,0		
	,#{ccgUseNy}
	,#{ccgOrder}
	,#{reference}	
)

	SELECT last_insert_id()

mapper.update:
UPDATE CCG  
SET 
codeGroup = #{codeGroup}
,codeGroupK = #{codeGroupK}
,ccgDelNy = #{ccgDelNy}
,ccgUseNy = #{ccgUseNy}
,ccgOrder = #{ccgOrder}
,reference = #{reference}
WHERE 1=1
	AND ccgSeq = #{ccgSeq}
