mapper.select:
SELECT
	a.*
	,(SELECT ccCodeName FROM CC b WHERE a.ifMmGender = b.ccSeq) as gender
	,(SELECT ccCodeName FROM CC b WHERE a.ifMmTel = b.ccSeq) as tel
	,(SELECT ccCodeName FROM CC b WHERE a.ifMmEmailAddress = b.ccSeq) as email
	,(SELECT ccCodeName FROM CC b WHERE a.ifMmMarketing = b.ccSeq) as marketing
FROM infrMember a
Where 1=1
AND ccDelNy = #{shDelNy}

		AND a.ccSeq = #{shValue}
		AND a.ccCodeName = #{shValue}
		AND a.codeGroupK = #{shValue}
		AND a.codeGroup = #{shValue}



		AND a.ccDate BETWEEN #{shStartDate} AND #{shEndDate}

mapper.insert:
INSERT INTO infrMember (
	ifMmName
	,ifMmGender
	,ifMmDob
	,ifMmId
	,ifMmPassWord
	,ifMmTel
	,ifMmPhone
	,ifMmEmail
	,ifMmEmailAddress
	,ifMmMarketin
	,ifMmSingupDate
	,ifMmAddress
	,ifMmAddress_homenumber
	,ifMmAddress_detail
	,ifMmNickname
)VALUES(
	#{ifMmName}
	,#{ifMmGender}
	,#{ifMmDob}
	,#{ifMmId}
	,#{ifMmPassWord}
	,#{ifMmTel}
	,#{ifMmPhone}
	,#{ifMmEmail}
	,#{ifMmEmailAddress}
	,#{ifMmMarketin}
	,#{ifMmSingupDate}
	,#{ifMmAddress}
	,#{ifMmAddress_homenumber}
	,#{ifMmAddress_detail}
	,#{ifMmNickname}
)

	SELECT last_id_insert_id()
