mapper.sql:
    	FROM infrMember a
		Where 1=1
		AND ifMmDelNy = #{shDelNy}

				AND a.ifMmSeq = #{shValue}
				AND a.ifMmName LIKE CONCAT ('%', #{shValue}, '%')
				AND a.ifMmId LIKE CONCAT ('%', #{shValue}, '%')
				AND a.ifMmGender LIKE CONCAT ('%', #{shValue}, '%')



				AND a.ifMmDob BETWEEN #{shStartDate} AND #{shEndDate}

mapper.insert:
