mapper.update:
	UPDATE seat e
	INNER JOIN screen c ON c.scSeq = e.scSeq
	INNER JOIN Theater d ON d.thSeq = c.Theater_thSeq
	INNER JOIN date b ON b.screen_scSeq = c.scSeq
	INNER JOIN Movie a ON a.mSeq  = b.Movie_mSeq  
	SET
		stUseNy = 0
	Where 1=1
		AND mSeq= #{mSeq}
			AND thLocation = #{thLocation}
			AND thName like #{thName}
			AND DATE_FORMAT(b.dDate, '%Y-%m-%d') = #{dDate}
            AND b.dTime = #{dTime}
            AND c.scNumber = #{scNumber}
            AND e.stRow = #{stRow}
            AND e.stCol = #{stCol}
            AND e.stUseNy = 0

mapper.insert:
INSERT INTO purchaseTicket (
	ifMmName
	,ifMmId
	,mNameKor
	,src
	,mAgeLimit
	,dDate
	,dTime
	,scScreenType
	,scNumber
	,stRow
	,stCol
	,stUseNy
	,stPrice
	,thName
	,aid
	,tid
)Values (
	#{ifMmName}
	,#{ifMmId}
	,#{mNameKor}
	,#{src}
	,#{mAgeLimit}
	,#{dDate}
	,#{dTime}
	,#{scScreenType}
	,#{scNumber}
	,#{stRow}
	,#{stCol}
	,1
	,#{stPrice}
	,#{thName}
	,#{aid}
	,#{tid}
)

	SELECT last_insert_id()

mapper.update:
UPDATE seat
SET
	stUseNy = 1
	WHERE 1=1
		AND stRow = #{stRow}
		AND stCol = #{stCol}
