mapper.update:
	UPDATE seat e
	INNER JOIN screen c ON c.scSeq = e.scSeq
	INNER JOIN Theater d ON d.thSeq = c.Theater_thSeq
	INNER JOIN date b ON b.screen_scSeq = c.scSeq
	INNER JOIN Movie a ON a.mSeq  = b.Movie_mSeq  
	SET
		stUseNy = 0
	Where 1=1
		AND mSeq= #{mSeq}
			AND thLocation = #{thLocation}
			AND thName like #{thName}
			AND DATE_FORMAT(b.dDate, '%Y-%m-%d') = #{dDate}
            AND b.dTime = #{dTime}
            AND c.scNumber = #{scNumber}
            AND e.stRow = #{stRow}
            AND e.stCol = #{stCol}
            AND e.stUseNy = 0

mapper.insert:
INSERT INTO purchaseTicket (
	ifMmSeq
	,ifMmName
	,ifMmId
	,mNameKor
	,mSeq
	,src
	,mAgeLimit
	,dSeq
	,dDate
	,dTime
	,scTotalSeat
	,scScreenType
	,scNumber
	,scRow
	,scCol
	,stRow
	,stCol
	,stUseNy
	,stPrice
	,stSeq
	,thLocation
	,thName
)Values (
	#{ifMmSeq}
	,#{ifMmName}
	,#{ifMmId}
	,#{mNameKor}
	,#{mSeq}
	,#{src}
	,#{mAgeLimit}
	,#{dSeq}
	,#{dDate}
	,#{dTime}
	,#{scTotalSeat}
	,#{scScreenType}
	,#{scNumber}
	,#{scRow}
	,#{scCol}
	,#{stRow}
	,#{stCol}
	,1
	,#{stPrice}
	,#{stSeq}
	,#{thLocation}
	,#{thName}
)

	SELECT last_insert_id()
